package Pratice;

public class EvenOddNumberProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int numb= 2; 
      if(numb/ 2 ==0)
      {
    	  System.out.println("Number is even");
      }else {
    	  System.out.println("Number is odd");
      }
	}

}
