package patternAssignment;

public class pattern17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		int letter=64;
		System.out.print("\nYour pattern here\n\n");
		for(i=1; i<=i; i++){
		  for(j=i; j>=i; j--){
		  System.out.print((char)(j+letter));
		}
		System.out.println();

	}

	}
}
