package patternAssignment;

public class patternHw1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=5; i++)
		{
			for(int j=1;j<=5;j++)
			{
			if (j==1 || j==5 || i==j )
			{
			System.out.print("N");	
			}
			else
				System.out.print(" ");
			}
			
		System.out.println();	
	
	}

}


}